import React, { useState } from "react";
import "./Calculator.css";
import Container from "@mui/material/Container";
import { Box } from "@mui/system";

export default function Calculator() {
  const [num, number] = useState(0);
  const [oldnum, numberTwo] = useState(0);
  const [operator, operatorString] = useState();

  function inputNum(e) {
    let input = e.target.value;
    if (num === 0) {
      number(input);
    } else {
      number(num + input);
    }
  }

  function clear() {
    number(0);
  }

  function porcentage() {
    number(num / 100);
  }

  function changeSign() {
    if (num > 0) {
      number(-num);
    } else {
      number(Math.abs(num));
    }
  }

  function operatorHandler(e) {
    let operatorInput = e.target.value;
    operatorString(operatorInput);
    numberTwo(num);
    number(0);
  }

  function calculate() {
    if (operator === "/") {
      number(parseFloat(oldnum) / parseFloat(num));
    } else if (operator === "X") {
      number(parseFloat(oldnum) * parseFloat(num));
    } else if (operator === "-") {
        console.log(oldnum)
        console.log(num)
        console.log(oldnum-num)
      number(parseFloat(oldnum) - parseFloat(num));
    } else if (operator === "+") {
      number(parseFloat(oldnum) + parseFloat(num));
    }
  }

  return (
    <div>
      <Box m={5} />
      <Container maxWidth="xs">
        <div className="wrapper">
          <Box m={12} />
          <h1 className="result">{num}</h1>
          <button onClick={clear}>AC</button>
          <button onClick={changeSign}>+/-</button>
          <button onClick={porcentage}>%</button>
          <button className="orange" onClick={operatorHandler} value="/"> / </button>
          <button className="gray" onClick={inputNum} value={7}> 7 </button>
          <button className="gray" onClick={inputNum} value={8}> 8 </button>
          <button className="gray" onClick={inputNum} value={9}> 9 </button>
          <button className="orange" onClick={operatorHandler} value="X"> X </button>
          <button className="gray" onClick={inputNum} value={4}> 4 </button>
          <button className="gray" onClick={inputNum} value={5}> 5 </button>
          <button className="gray" onClick={inputNum} value={6}> 6 </button>
          <button className="orange" onClick={operatorHandler} value="-"> - </button>
          <button className="gray" onClick={inputNum} value={1}> 1 </button>
          <button className="gray" onClick={inputNum} value={2}> 2 </button>
          <button className="gray" onClick={inputNum} value={3}> 3 </button>
          <button className="orange" onClick={operatorHandler} value="+"> + </button>
          <button className="gray" onClick={inputNum} value={0}> 0 </button>
          <button className="gray" onClick={inputNum} value={"."}> , </button>
          <button className="gray" style={{ visibility: "hidden" }}> , </button>
          <button className="orange" onClick={calculate}> = </button>
        </div>
      </Container>
    </div>
  );
}
